def is_valid_parentheses(s: str) -> bool:
    stack = []
    bracket_map = {')': '(', '}': '{', ']': '['}
    for char in s:
        if (char in bracket_map.values()):
            stack.append(char)
        elif (char in bracket_map.keys()):
            if ((not stack) or (stack.pop() != bracket_map[char])):
                return False
    return (not stack)

