def count_vowels_case_insensitive(input_string):
    vowels = 'aeiou'
    vowel_count = {vowel: 0 for vowel in vowels}
    input_string = input_string.lower()
    for char in input_string:
        if (char in vowels):
            vowel_count[char] += 1
    return vowel_count
